import React, { useState, useEffect } from "react";
import "../styles/Game.css";
import { collection, getDocs } from "firebase/firestore";
import { db } from "../config/firebase";

const categories = ["Game PS4", "Game PS5", "Game Switch", "Game Nintendo"];

export default function Game() {
  const [activeCategory, setActiveCategory] = useState("Game PS4");
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  // Hàm truy vấn Firestore
  const fetchGames = async (category) => {
    try {
      setLoading(true);

      // Lấy tên document theo category
      const platform = category.replace("Game ", ""); // "Game PS4" -> "PS4"

      // Truy vấn collection con, ví dụ: Game/PS4/FIFA
      const fifaCollection = collection(db, "Game", platform, "FIFA");
      const fifaSnapshot = await getDocs(fifaCollection);

      const gameList = fifaSnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(), // có Name, Image, Price
      }));

      setGames(gameList);
    } catch (error) {
      console.error("Lỗi khi truy vấn Firestore:", error);
    } finally {
      setLoading(false);
    }
  };

  // Load game khi đổi category
  useEffect(() => {
    fetchGames(activeCategory);
  }, [activeCategory]);

  return (
    <div className="game-page">
      {/* Breadcrumb */}
      <div className="breadcrumb">
        <span>Trang chủ</span> <span>/</span>
        <span>Sản phẩm</span> <span>/</span>
        <span className="current">Game</span>
      </div>

      {/* Bộ lọc loại game */}
      <div className="filter-row">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`filter-btn ${activeCategory === cat ? "active" : ""}`}
            onClick={() => setActiveCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Lưới sản phẩm */}
      {loading ? (
        <p>Đang tải game...</p>
      ) : (
        <div className="game-grid">
          {games.map((game) => (
            <div key={game.id} className="game-card">
              <img src={game.Image} alt={game.Name} />
              <h3>{game.Name}</h3>
              <span className="platform">Giá: {game.Price}₫</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
