import React, { useState } from "react";
import "../styles/Game.css";

const categories = [
  "Game PS5",
  "Game PS4",
  "Game Switch",
  "Game Nintendo",
  "Game Collection",
  "Game Digital",
  "Game Secondhand",
];

const games = [
  {
    id: 1,
    title: "Mario Kart World",
    img: "/images/mario-kart.jpg",
    platform: "Switch",
  },
  {
    id: 2,
    title: "Hokko Life",
    img: "/images/hokko-life.jpg",
    platform: "Switch",
  },
  {
    id: 3,
    title: "Mario Golf Super Rush",
    img: "/images/mario-golf.jpg",
    platform: "Switch",
  },
  {
    id: 4,
    title: "Touhou Luna Nights",
    img: "/images/luna-nights.jpg",
    platform: "Switch",
  },
];

export default function GamePage() {
  const [activeCategory, setActiveCategory] = useState("Game PS5");

  return (
    <div className="game-page">
      {/* Bộ lọc loại game */}
      <div className="filter-row">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`filter-btn ${activeCategory === cat ? "active" : ""}`}
            onClick={() => setActiveCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Bộ lọc phụ */}
      <div className="sub-filters">
        <select>
          <option>Thể loại</option>
          <option>Hành động</option>
          <option>Phiêu lưu</option>
          <option>Thể thao</option>
        </select>
        <select>
          <option>Số người chơi</option>
          <option>1 Người</option>
          <option>2 Người</option>
          <option>Multiplayer</option>
        </select>
        <select>
          <option>Sắp xếp mặc định</option>
          <option>Giá tăng dần</option>
          <option>Giá giảm dần</option>
          <option>Mới nhất</option>
        </select>
      </div>

      {/* Lưới sản phẩm */}
      <div className="game-grid">
        {games.map((game) => (
          <div key={game.id} className="game-card">
            <img src={game.img} alt={game.title} />
            <h3>{game.title}</h3>
            <span className="platform">{game.platform}</span>
          </div>
        ))}
      </div>
    </div>
  );
}
