import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Console.css";
import ps5 from "../images/ps5.png";
import ps4 from "../images/ps4.png";
import nintendo from "../images/nintendo.webp";
import xbox from "../images/xbox.webp";
import handheld from "../images/handheld.webp";
import metaquest from "../images/metaquest.webp";
import retro from "../images/retro.webp";

const consoles = [
  { id: 1, name: "PS5", image: ps5 },
  { id: 2, name: "PS4", image: ps4 },
  { id: 3, name: "Nintendo", image: nintendo },
  { id: 4, name: "Xbox", image: xbox },
  { id: 5, name: "Handheld PC", image: handheld },
  { id: 6, name: "Meta Quest", image: metaquest },
  { id: 7, name: "Retro", image: retro },
];

// Dữ liệu mock để demo
const mockProducts = {
  PS5: [
    { id: 1, name: "PS5 Slim", price: "14.000.000₫", image: "/images/ps5.png" },
    { id: 2, name: "PS5 Controller", price: "1.800.000₫", image: "/images/ps5-controller.png" },
  ],
  PS4: [
    { id: 3, name: "PS4 Pro", price: "7.500.000₫", image: "/images/ps4.png" },
    { id: 4, name: "PS4 Controller", price: "900.000₫", image: "/images/ps4-controller.png" },
  ],
  Xbox: [
    { id: 5, name: "Xbox Series X", price: "13.500.000₫", image: "/images/xbox.webp" },
    { id: 6, name: "Xbox Controller", price: "1.500.000₫", image: "/images/xbox-controller.png" },
  ],
  Nintendo: [
    { id: 7, name: "Nintendo Switch OLED", price: "8.500.000₫", image: "/images/nintendo.webp" },
  ],
  // thêm cho Meta Quest, Retro, Handheld PC...
};

export default function Console() {
  const [selectedConsole, setSelectedConsole] = useState("PS5");

  return (
    <div className="console-page">
      {/* Breadcrumb tĩnh */}
      <div className="breadcrumb">
        <span>Trang chủ</span> <span>/</span>
        <span>Sản phẩm</span> <span>/</span>
        <span className="current">Máy Game</span>
    </div>

      <h1>Máy Game</h1>

      {/* Danh sách console */}
      <div className="console-list">
        {consoles.map((item) => (
          <div
            key={item.id}
            className={`console-card ${selectedConsole === item.name ? "active" : ""}`}
            onClick={() => setSelectedConsole(item.name)}
          >
            <img src={item.image} alt={item.name} />
            <p>{item.name}</p>
          </div>
        ))}
      </div>

      {/* Danh sách sản phẩm */}
      <div className="product-list">
        <h2>Sản phẩm {selectedConsole}</h2>
        <div className="products">
          {mockProducts[selectedConsole]?.length > 0 ? (
            mockProducts[selectedConsole].map((p) => (
              <div key={p.id} className="product-card">
                <img src={p.image} alt={p.name} />
                <h3>{p.name}</h3>
                <p>{p.price}</p>
                <button>Thêm vào giỏ</button>
              </div>
            ))
          ) : (
            <p>Chưa có sản phẩm</p>
          )}
        </div>
      </div>
    </div>
  );
}
