import React, { useState, useEffect } from "react";
import "../styles/Game.css";
import { db } from "../config/firebase";
import { doc, getDocs, listCollections } from "firebase/firestore";

const categories = [
  "Game PS5",
  "Game PS4",
  "Game Switch",
  "Game Nintendo",
  "Game Collection",
  "Game Digital",
  "Game Secondhand",
];

export default function Game() {
  const [activeCategory, setActiveCategory] = useState("Game PS4");
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  // 🔎 Hàm lấy game theo platform (PS4, PS5…)
  const fetchGames = async (category) => {
    try {
      setLoading(true);

      // Bỏ chữ "Game " để lấy platform
      const platform = category.replace("Game ", ""); // "PS4"

      // Trỏ tới document platform (vd: Game/PS4)
      const platformDoc = doc(db, "Game", platform);

      // Lấy toàn bộ collection con trong document đó (vd: FIFA, PES…)
      const subCollections = await listCollections(platformDoc);

      let allGames = [];

      for (const subCol of subCollections) {
        const snapshot = await getDocs(subCol);

        const gameList = snapshot.docs.map((d) => ({
          id: d.id,
          ...d.data(),
          series: subCol.id, // để biết thuộc series nào (FIFA, PES…)
        }));

        allGames = [...allGames, ...gameList];
      }

      setGames(allGames);
    } catch (err) {
      console.error("❌ Lỗi Firestore:", err);
    } finally {
      setLoading(false);
    }
  };

  // 🔄 Tự fetch khi đổi category
  useEffect(() => {
    fetchGames(activeCategory);
  }, [activeCategory]);

  return (
    <div className="game-page">
      {/* Breadcrumb */}
      <div className="breadcrumb">
        <span>Trang chủ</span> <span>/</span>
        <span>Sản phẩm</span> <span>/</span>
        <span className="current">Game</span>
      </div>

      {/* Bộ lọc loại game */}
      <div className="filter-row">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`filter-btn ${activeCategory === cat ? "active" : ""}`}
            onClick={() => setActiveCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Bộ lọc phụ */}
      <div className="sub-filters">
        <select>
          <option>Thể loại</option>
          <option>Hành động</option>
          <option>Phiêu lưu</option>
          <option>Thể thao</option>
        </select>
        <select>
          <option>Số người chơi</option>
          <option>1 Người</option>
          <option>2 Người</option>
          <option>Multiplayer</option>
        </select>
        <select>
          <option>Sắp xếp mặc định</option>
          <option>Giá tăng dần</option>
          <option>Giá giảm dần</option>
          <option>Mới nhất</option>
        </select>
      </div>

      {/* Lưới sản phẩm */}
      {loading ? (
        <p>⏳ Đang tải game...</p>
      ) : games.length === 0 ? (
        <p>Không có game nào.</p>
      ) : (
        <div className="game-grid">
          {games.map((game) => (
            <div key={game.id} className="game-card">
              <img src={game.Image} alt={game.Name} />
              <h3>{game.Name}</h3>
              <span className="platform">{game.series}</span>
              <p className="price">{game.Price}₫</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
