import React, { useState, useEffect } from "react";
import "../styles/Game.css";
import { collection, getDocs, query, where } from "firebase/firestore";
import { db } from "../f";

const categories = [
  "Game PS5",
  "Game PS4",
  "Game Switch",
  "Game Nintendo",
  "Game Collection",
  "Game Digital",
  "Game Secondhand",
];

export default function Game() {
  const [activeCategory, setActiveCategory] = useState("Game PS5");
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);

  // Hàm truy vấn Firestore
  const fetchGames = async (category) => {
    try {
      setLoading(true);
      let q;
      if (category) {
        q = query(
          collection(db, "games"),
          where("category", "==", category)
        );
      } else {
        q = collection(db, "games");
      }
      const querySnapshot = await getDocs(q);
      const gameList = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setGames(gameList);
    } catch (error) {
      console.error("Lỗi khi truy vấn Firestore:", error);
    } finally {
      setLoading(false);
    }
  };

  // Lấy game khi load trang hoặc đổi category
  useEffect(() => {
    fetchGames(activeCategory);
  }, [activeCategory]);

  return (
    <div className="game-page">
      {/* Breadcrumb */}
      <div className="breadcrumb">
        <span>Trang chủ</span> <span>/</span>
        <span>Sản phẩm</span> <span>/</span>
        <span className="current">Game</span>
      </div>

      {/* Bộ lọc loại game */}
      <div className="filter-row">
        {categories.map((cat) => (
          <button
            key={cat}
            className={`filter-btn ${activeCategory === cat ? "active" : ""}`}
            onClick={() => setActiveCategory(cat)}
          >
            {cat}
          </button>
        ))}
      </div>

      {/* Bộ lọc phụ */}
      <div className="sub-filters">
        <select>
          <option>Thể loại</option>
          <option>Hành động</option>
          <option>Phiêu lưu</option>
          <option>Thể thao</option>
        </select>
        <select>
          <option>Số người chơi</option>
          <option>1 Người</option>
          <option>2 Người</option>
          <option>Multiplayer</option>
        </select>
        <select>
          <option>Sắp xếp mặc định</option>
          <option>Giá tăng dần</option>
          <option>Giá giảm dần</option>
          <option>Mới nhất</option>
        </select>
      </div>

      {/* Lưới sản phẩm */}
      {loading ? (
        <p>Đang tải game...</p>
      ) : (
        <div className="game-grid">
          {games.map((game) => (
            <div key={game.id} className="game-card">
              <img src={game.img} alt={game.title} />
              <h3>{game.title}</h3>
              <span className="platform">{game.platform}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
