// App.js (chỉ phần useEffect)
import React, { useEffect } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
// ... các import khác

function App() {
  useEffect(() => {
    const setHeaderHeight = () => {
      const headerEl = document.querySelector('.header');
      if (!headerEl) return;
      // Đảm bảo header trả về kích thước thực tế
      headerEl.style.removeProperty('height'); // remove nếu có height inline
      // dùng next frame để chắc chắn layout đã cập nhật
      requestAnimationFrame(() => {
        const h = headerEl.offsetHeight;
        // gán biến CSS toàn cục
        document.documentElement.style.setProperty('--header-height', `${h}px`);
      });
    };

    // gọi lần đầu và lắng nghe resize + fontchange (nếu cần)
    setHeaderHeight();
    window.addEventListener('resize', setHeaderHeight);
    // optional: bắt mutation (nếu header nội dung thay đổi động)
    const obs = new MutationObserver(setHeaderHeight);
    const headerEl = document.querySelector('.header');
    if (headerEl) obs.observe(headerEl, { childList: true, subtree: true, attributes: true });

    return () => {
      window.removeEventListener('resize', setHeaderHeight);
      if (obs) obs.disconnect();
    };
  }, []);

  return (
    <Router>
      <Header />
      <Routes> ... </Routes>
      <Footer />
    </Router>
  );
}

export default App;
