import React, { useState } from "react";
import { Link } from "react-router-dom";
import "../styles/Header.css";
import logo from "../images/Logo.jpg";

export default function Header() {
  const [cartCount, setCartCount] = useState(0);

  return (
    <header className="header">
      <div className="header-container">
        {/* Logo */}
        <Link to="/" className="logo">
          <img src={logo} alt="TWGameStore" />
        </Link>

        {/* Thanh tìm kiếm */}
        <div className="search-bar">
          <input type="text" placeholder="Tìm kiếm game..." />
          <button>Tìm</button>
        </div>

        {/* Navigation */}
        <nav className="nav">
          <ul>
            <li><Link to="/">Trang Chủ</Link></li>

            {/* Dropdown Sản Phẩm */}
            <li className="dropdown">
              <Link to="/Product">Sản Phẩm</Link>
              <ul className="dropdown-menu">
                <li><Link to="/Product/action">Action</Link></li>
                <li><Link to="/Product/rpg">RPG</Link></li>
                <li><Link to="/Product/simulation">Simulation</Link></li>
              </ul>
            </li>

            {/* Dropdown Chủ Đề Game */}
            <li className="dropdown">
              <Link to="/about">Chủ Đề Game</Link>
              <ul className="dropdown-menu">
                <li><Link to="/about/pc">PC</Link></li>
                <li><Link to="/about/mobile">Mobile</Link></li>
                <li><Link to="/about/console">Console</Link></li>
              </ul>
            </li>

            <li><Link to="/contact">Liên Hệ</Link></li>
          </ul>
        </nav>

        {/* Giỏ hàng */}
        <div className="cart">
          <Link to="/cart">
            <div className="cart-icon-wrapper">
              <i className="fas fa-shopping-cart"></i>
              {cartCount > 0 && <span className="cart-count">{cartCount}</span>}
            </div>
          </Link>
        </div>
      </div>
    </header>
  );
}
